import React, { useState } from "react";

const Forms = (props) => {
  const [details, setDetails] = useState({
    name: "",
    department: "",
    rating: "",
  });
  const updateInput = (event, ch) => {
    details[ch] = event.target.value;
    setDetails(details);
  };

  return (
    <div>
      <form>
        <div>
          <h1>EMPLOYEE FORM HANDLING</h1>
          <label>
            Name :
            <input
              className="name"
              onChange={(event) => updateInput(event, "name")}
            />
          </label>
          <br />
          <label>
            Department :
            <input
              className="department"
              onChange={(event) => updateInput(event, "department")}
            />
          </label>
          <br />
          <label>
            Rating :
            <input
              className="rating"
              onChange={(event) => updateInput(event, "rating")}
            />
          </label>
          <br />
          <button onClick={() => props.add_details()} type="button">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};
export default Forms;
