import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation, useNavigate } from "react-router-dom";
import { editData } from "./Slice";

function Edit() {
  const location = useLocation(); // returns the location object
  const navigate = useNavigate();
  const index = location.state.data;
  const dispatch = useDispatch();
  const array = useSelector((state) => state.data); //

  // array of objects : context.entries
  // function         : context.updateFunction

  // the object in which the changes will be done
  const newObj = {
    name: array[index].name,
    id: array[index].id,
  };

  console.log("Index : " + index);
  const handleChange = (event) => {
    newObj[event.target.name] = event.target.value;
    // console.log(newObj);
  };

  const handleUpdate = (event) => {
    // console.log(context.entries[index])
    dispatch(editData({ newObj, index }));

    //    context.updateFunction(
    //     (prevObj)=>{
    //         prevObj[index] = newObj;
    //         return (prevObj) //  updated prevObj is the new state
    //     }
    //    );
    navigate("/");
  };

  return (
    <>
      <input
        onChange={handleChange}
        name="name"
        placeholder="Name"
        defaultValue={array[index].name}
      ></input>

      <input
        onChange={handleChange}
        name="age"
        placeholder="Age"
        defaultValue={array[index].age}
      ></input>
      <br />
      <input
        onChange={handleChange}
        name="course"
        placeholder="course"
        defaultValue={array[index].course}
      ></input>
      <input
        onChange={handleChange}
        name="batch"
        placeholder="batch"
        defaultValue={array[index].batch}
      ></input>
      <br />
      <button onClick={handleUpdate}>Update</button>
    </>
  );
}

export default Edit;
