import React, { useState } from "react";
const [details, setDetails] = useState({
  name: "",
  discription: "",
  rating: "",
});
const updateInput = (event, ch) => {
  setDetails((details[ch] = event.target.value));
  // switch (ch) {
  //   case "name":
  //     break;
  //     case "discription":
  //     break;
  //     case "rating":
  //       break;

  // }
};
const forms = (props) => {
  return (
    <div>
      <form>
        <div>
          <h1>EMPLOYEE FORM HANDLING</h1>
          <label>
            Name :
            <input className="name" onChange={updateInput(event, "name")} />
          </label>
          <br />
          <label>
            Department :
            <input className="department" />
          </label>
          <br />
          <label>
            Rating :
            <input className="rating" />
          </label>
          <br />
          <button onClick={() => props.add_details()} type="button">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};
export default forms;
